    function resendEmailVerification() {
        //Hiding the buttons
        var spResend = jQuery('#spResend');
        var imgSpinner = jQuery('#imgSpinner');
        spResend.hide();
        imgSpinner.show();

        //The AjaxPro server side method is in profile_email.aspx.vb.
        var resend = uship.ns.ajaxpro.register('profile_email', 'ResendInvitation', []);
        resend(resendEmailVerificationCallback);
    }

    function resendEmailVerificationCallback(response) {
        var json = response.value;
        var imgSpinner = jQuery("#imgSpinner");
        imgSpinner.hide();

        var spEmailConfirmation = jQuery('#spEmailConfirmation');
        spEmailConfirmation.text(json.message);

        //This is a hack for IE.  
        //For some reason when hiding the spinner it leaves 
        //an empty area where the spinner was, over the confirmation text.  If you move your mouse,
        //the text will eventually show up.
        //I just call show on the confirmation message after a little bit of time.
        setTimeout(function () {spEmailConfirmation.show(); }, 100);
    }
