            var initializeMainDimensions = function() {
                iMD = {};
                iMD.isResponsive = 'true';
                iMD.main = $('.responsive-page .main');
                iMD.windowWidth = 0;
                iMD.windowHeight = 0;
                iMD.footer = $('.responsive-page #ft');
                iMD.footerHeight = 0;

                function init() {
                    if(iMD.isResponsive === 'true') {
                        iMD.resizeMain();

                        $(window).resize(function() {
                            iMD.resizeMain();
                        });   
                    }
                }
                
                iMD.getWindowWidth = function () {
                    return window.innerWidth || window.document.documentElement.clientWidth || window.document.body.clientWidth || 0;
                }

                iMD.getWindowHeight = function () {
                    return window.innerHeight || window.document.documentElement.clientHeight || window.document.body.clientHeight || 0;
                }

                iMD.resizeMain = function () {
                    iMD.windowWidth = iMD.getWindowWidth();
                    
                    if(iMD.main && iMD.footer && iMD.windowWidth < 768) {
                        iMD.windowHeight = iMD.getWindowHeight();
                        iMD.footerHeight = iMD.footer.height();
                        
                        if(iMD.footerHeight) {
                            var minHeight = iMD.windowHeight - iMD.footerHeight;
                            iMD.main.css('min-height', minHeight);
                        }
                        else if (iMD.windowHeight > 0) {
                            iMD.main.css('min-height', iMD.windowHeight);
                        }
                    }
                    else if (iMD.windowWidth > 767) {
                        iMD.main.css('min-height', '');
                    }
                }

                init();
                return iMD;
            }

            
            initializeMainDimensions();
            
        });
    });
